import React, { Component } from 'react';
import TodoItem from './TodoItem';

const todos = [
    {id: 1, title: "Сделать уроки", complete: false}, 
    {id: 2, title: "Сделать дом", complete: false}, 
    {id: 3, title: "Купить хлеб", complete: false},
    {id: 4, title: "Купить молоко", complete: false},
]
    export default class TodoList extends Component{
    constructor(props){
        super(props);
        this.state={
            todos: [
                {id:1, title: "Сделать уроки", complete: false}, 
                {id:2, title: "Сделать дом", complete: false}, 
                {id:3, title: "Купить хлеб", complete: false},
                {id:4, title: "Купить молоко", complete: false},
            ],
        };
        this.onCheckedTodo = this.onCheckedTodo.bind(this);
    }
   
    onCheckedTodo(id){
        let todos = this.state.todos.map((value)=>{
            if (value.id === id){
                value.complete = !value.complete
            }
            return value
        })
        this.setState({todos})
    }
    
    render(){
        const {todos} = this.state;
        return(
        <ul>
            {todos.map((val)=>{
                return <TodoItem todo={val} key={val.id} checked={this.onCheckedTodo}/>
            })}  
        </ul>
        )
    }
}
