import React, { Component } from 'react';
import TodoItem from './TodoItem';

    export default class TodoList extends Component{
    constructor(props){
        super(props);
        this.state={
            todos: [],
            inputValue: ''
        };
        this.onCheckedTodo = this.onCheckedTodo.bind(this);
        this.addTodo = this.addTodo.bind(this);
        this.deleteTodo = this.deleteTodo.bind(this);
    }
   
    onCheckedTodo(id){
        let todos = this.state.todos.map((value)=>{
            if (value.id === id){
                value.complete = !value.complete
            }
            return value
        })
        this.setState({todos})
    }
    
    addTodo(event){
        event.preventDefault();
        if (this.state.inputValue <= 0){
            alert('Заполните значение')
        }
        else{
        let todos = this.state.todos.concat([{
               title: this.state.inputValue,
               id: Math.floor(Math.random()*1000),
               complete: false
        },
    ]);
    this.setState({todos})
        }
    }

    deleteTodo(id){
        let todos = this.state.todos.filter(val=>{
            return val.id !== id
        })
        this.setState({todos: todos})
    }

    componentDidMount(){
        fetch('http://jsonplaceholder.typicode.com/todos')
        .then(res => res.json())
        .then(res => this.setState({todos: res}))
    }


    render(){
        const {todos} = this.state;
        return(
        <ul className = "list-group">
            <form onSubmit={this.addTodo}>
             <input placeholder='type here' onChange={(event)=>{this.setState({inputValue: event.target.value})}}/>
             <button className = "btn btn-primary" type="submit">Add</button>
            </form>
            {todos.map((val)=>{
                return <TodoItem todo={val} key={val.id} checked={this.onCheckedTodo} deleteTodo={this.deleteTodo}/>
            })}  
        </ul>
        );
    }
}
