import React, { Component } from 'react';
import UserInfo from './UserInfo';
import { Link } from 'react-router-dom';
import { BrowserRouter as Router, Route } from 'react-router-dom';

const styles = {
    userContainer: {
        position: 'absolute',
        width: '450px',
        marginLeft: '40px'
    },
    infoContainer: {
        position: 'absolute',
        top: '7.5%',
        left: '50%',
        border: '1px solid rgba(0,0,0,.125)',
        width: '450px',
        height: '500px',
        padding: '.5rem 1rem',
        borderRadius: '5px'
    }
};

export default class UserList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: [],
        };
    }

    componentDidMount() {
        fetch('http://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(res => this.setState({ user: res }));
    }

    render() {
        return (
            <Router>
                <h3>Users</h3>
                <div className="main">
                    <div style={styles.usersContainer} className="container row">
                        <ul style={styles.usersContainer} class="list-group">
                            {this.state.user.map((val) => {
                                return (<Link to={`/users/id/${val.id}`} > {val.username} </Link>);
                            })}
                        </ul>
                    </div>
                    <div style={styles.infoContainer} className="info">
                        <Route path="/users/id/:uID"
                            render={({ match }) => (
                                <UserInfo user={this.state.user.find((u) => u.id == match.params.uID)} />
                            )}
                        />
                    </div>
                </div>
            </Router>
        )
    }
}
