import React, { Component } from 'react';
import UserItem from './UserItem';

const styles = {
    usersContainer: {
        position: 'absolute',
        width: '450px'
    },
    outputInfo: {
        position: 'absolute',
        top: '7.5%',
        left: '50%',
        border: '1px black solid',
        width: '400px',
        height: '600px',
    }
}
export default class UserList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: [],
            currentUser: 2
        };
        this.setCurrentUser = this.setCurrentUser.bind(this);
    }

    componentDidMount() {
        fetch('http://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(res => this.setState({ user: res }));
    }

    setCurrentUser(id) {
        this.setState({ currentUser: id })
    }

    render() {
        return (
            <div className="main">
                <div style={styles.usersContainer} className="container row">
                    <ul style={styles.usersContainer} class="list-group">
                        {this.state.user.map((val) => {
                            return <UserItem user={val} currentUser={this.state.currentUser} setCurrentUser={this.setCurrentUser} />;
                        })}
                    </ul>
                </div>
                <div className="info">
                    <form name="form2">
                        <output style={styles.outputInfo} class="infoUser" name="infoUser" >
                            {this.state.user.map((val) => {
                                if (val.id === idActive) {
                                    return <UserInfo user={val} />
                                }})}
                        </output>
                    </form>
                </div>
            </div>
        )
    }
}
