import React, {Component} from 'react';

export default class Pagination extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        const { todosPerPage, allTodos, currentPage } = this.props;
        const pageNumbers = [];

        for (let i = 1; i <= Math.ceil(allTodos / todosPerPage); i++) {
            pageNumbers.push(i);
        }
        return (
            <div>
                <ul className="pagination">
                    {pageNumbers.map(number => {
                        return(
                        <li className="page-item">
                            <a class="page-link" href="#">{number} onClick = {() => currentPage(number)}</a>
                        </li>)
                    })}
                </ul>
            </div>
        );
    }
}
